function setNewCepSearchField(e, o = []) {
    let e = $(e);
    function r() {
        e.removeClass("is-invalid"),
            o.forEach((e) => {
                $("#" + e).val("");
            });
    }
    r();
    let i = e.val().replace(/\D/g, "");
    if (i) {
        var l;
        ((l = i), /^[0-9]{8}$/.test(l))
            ? $.getJSON(
                  "https://viacep.com.br/ws/" + i + "/json/?callback=?",
                  function (e) {
                      if ("erro" in e)
                          console.error("CEP n\xe3o encontrado"), r();
                      else {
                          let i = {
                              address: e.logradouro,
                              neighborhood: e.bairro,
                              city: e.localidade,
                              state: e.uf,
                              complement: e.complemento,
                              ibge: e.ibge,
                          };
                          !(function e(r) {
                              Object.entries(r).forEach(([e, r]) => {
                                  let i = $("#" + e);
                                  i.length && o.includes(e) && i.val(r);
                              });
                          })(i);
                      }
                  }
              )
            : (console.error("CEP inv\xe1lido"), r());
    }
}
